"""    context.py        Game object container."""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *import objectclass context :        def __init__ ( self ) :        self.reset()    #/        def reset ( self ) :        self.object_list = []    #/        def events ( self ) :        pass    #/        def tick ( self ) :        for o in self.object_list :            if o is not None :                o.tick()    #/        def draw ( self , wsurface ) :        for o in self.object_list :            if o is not None :                o.draw( wsurface )    #/        def addObject ( self , new_object) :        """ adds an object to the object list, filling up any available empty spaces """        # try to find an empty slot        for o , position in enumerate( self.object_list ) :            if o is None :                self.object_list[ position ] = new_object                self.object_list[ position ].load() # load the new object's resources                return        # if we didn't find an empty space, append the new object at the end        self.object_list.append( new_object )        self.object_list[ -1 ].load() # load the new objects resources    #/    #/