"""    control.py        Part of the game package. Provides an interface to control the rest of the package"""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *from state import stateimport playerdef play ( level_filename ) :    """ sets the state of the package to play a level """    state.playing = True    # add the player    p = player.player()    state.c.addObject( p )#/def stop () :    """ sets the state of the package to be dormant and frees any memory used by the previous game played """    state.reset(state.i)#/def setPause ( p ) :    state.paused = p    # update context    state.c.setPause( p )#/def events() :    """ handles events for the game """    if not state.playing == True :        return#/def tick() :    """ updates the game """    if not state.playing == True :        return    # update the context    state.c.tick()#/def draw ( wsurface ) :    """ draws the game """    if not state.playing == True :        return    # draw the context    state.c.draw( wsurface )#/