"""    gameinfo.py        Class to load gameinfo files and provide it's data in useful formats for other classes"""import sysimport osimport mathimport xml.etree.ElementTree as etsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfclass gameinfo :    def __init__ ( self ) :        self.reset()    #/        def reset ( self ) :        # filename        self.filename = ""        # general level info        self.name = ""        # asset filenames        self.fname_texture_background = ""    #/        def load ( self , filename , verbose = True) :        self.filename = filename        if verbose :            out.pl( "Loading level from gameinfo file '" + self.filename + "'" )    #/#/