"""    object.py        Base class for all game objects."""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *parentdir = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))os.sys.path.insert(0,parentdir)import resourceclass object :    def __init__ ( self ) :        self.paused = False        self.x = 0        self.y = 0        self.x_speed = 0        self.y_speed = 0        self.hp = 0        self.maxhp = 0        self.radius = 0        self.dead = False        self.hasBeenOnScreen = False        self.isOnScreen = False    #/        def warp ( self , new_x , new_y ) :        self.x = new_x        self.y = new_y    #/        def setPause ( self , p ) :        self.paused = p        if self.paused == True :            self.gamePaused()        elif self.paused == False :            self.gameUnpaused()    #/        def gamePaused ( self ) :        """ ment to be overridden. called when the game is paused """    #/        def gameUnpaused ( self ) :        """ ment to be overridden. called when the game is unpaused """    #/        def load ( self ) :        """ commonly called by 'context' when it's added to the object list """        pass    #/        def events ( self , event) :        pass    #/        def tick ( self ) :        self.x += self.x_speed        self.y += self.y_speed                # update isOnScreen and hasBeenOnScreen        if self.x+self.radius < 0 or self.x-self.radius > 640 or self.y+self.radius < 0 or self.y-self.radius > 480 :            self.isOnScreen = False        else :            self.isOnScreen = True        if self.hasBeenOnScreen == False and self.isOnScreen == True :            self.hasBeenOnScreen = True    #/        def draw ( self , wsurface ) :        pass    #/#/