"""    player.py        A game 'object' that represents the player"""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *import objectparentdir = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))os.sys.path.insert(0,parentdir)from resource import TEXTURES,FONTSclass player ( object.object ) :    def __init__ ( self ) :        object.object.__init__( self )                TEXTURES.player40 = "graphics/characters/player40.png"        self.texture = 0                self.x_acceleration = 0.1        self.y_acceleration = 0.1                self.MOVE_LEFT = False        self.MOVE_RIGHT = False        self.MOVE_UP = False        self.MOVE_DOWN = False    #/        def load ( self ) :        self.texture = TEXTURES.player40    #/        def events ( self , events ) :        keys = pygame.key.get_pressed()        # do movement controls        if keys[pygame.K_LEFT] or keys[pygame.K_a] :            self.MOVE_LEFT = True        else :            self.MOVE_LEFT = False        #        if keys[pygame.K_RIGHT] or keys[pygame.K_d] :            self.MOVE_RIGHT = True        else :            self.MOVE_RIGHT = False        #        if keys[pygame.K_UP] or keys[pygame.K_w] :            self.MOVE_UP = True        else :            self.MOVE_UP = False        #        if keys[pygame.K_DOWN] or keys[pygame.K_s] :            self.MOVE_DOWN = True        else :            self.MOVE_DOWN = False    #/        def tick ( self ) :        # do physics        if self.MOVE_LEFT :            self.x_speed -= self.x_acceleration        if self.MOVE_RIGHT :            self.x_speed += self.x_acceleration        if self.MOVE_UP :            self.y_speed -= self.y_acceleration        if self.MOVE_DOWN :            self.y_speed += self.y_acceleration        # update position        self.x += self.x_speed        self.y += self.y_speed    #/        def gamePaused ( self ) :        pass    #/        def gameUnpaused ( self ) :        pass    #/        def draw ( self , wsurface) :        wsurface.blit( self.texture , (self.x , self.y) )    #/#/