"""    player.py        A game 'object' that represents the player"""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *import objectparentdir = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))os.sys.path.insert(0,parentdir)from resource import TEXTURES,FONTSclass player ( object.object ) :    def __init__ ( self ) :        object.object.__init__( self )        self.x_speed = 1        self.y_speed = 0.5        TEXTURES.player40 = "graphics/characters/player40.png"        self.texture = 0        self.visible = True    #/        def load ( self ) :        self.texture = TEXTURES.player40    #/        def gamePaused ( self ) :        self.visible = False    #/        def gameUnpaused ( self ) :        self.visible = True    #/        def draw ( self , wsurface) :        if self.visible :            wsurface.blit( self.texture , (self.x , self.y) )    #/#/