"""    label.py        a gui class that displays text     - with text wrap"""import sysimport osimport mathsys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport pygamefrom pygame.locals import *class label:        def __init__ ( self ):        # interface        self.x = 0        self.y = 0        self.width = 0        self.height = 0        self.font = -1        self.text = ""        self.textcolour = (255,255,255)        self.backcolour = (255,255,255)        self.drawback = False        self.wordwrap = False        # font info        self.fontheight = -1        # individual lines of text to be rendered        self.textlines = []        # rendered surfaces        self.text_surfaces = []        self.background = -1        def update ( self ):        """ (re)calculate the label """        # get the font height        tmp = -1        (tmp,self.fontheight) = self.font.size("I")        # clear old values        self.textlines = [""]        self.text_surfaces = []        if self.wordwrap:            # place line breaks in the text to wrap text            # doesn't break up words            cur_line_len = 0            cur_word = ""            for c in self.text:                charlen = -1                (charlen,tmp) = self.font.size(c)                cur_line_len += charlen                if not c == " ":                    cur_word += c                else:                    if cur_line_len >= self.width:                        cur_line_len = 0                        self.textlines.append("")                    self.textlines[-1] += cur_word + " "                    cur_word = ""        else:            # no word-wrap            # place all the text on the first line            self.textlines[0] = self.text        # pre-render the text        # render the surface(s)        for t in self.textlines:            if self.drawback:                self.text_surfaces.append( self.font.render( t , False , self.textcolour ) )            else:                self.text_surfaces.append( self.font.render( t , False , self.textcolour ) )        def draw ( self , wsurface ):        global fuck_constant_b        """ blits the pre-rendered surfaces """        if self.drawback:            # fill the background section            wsurface.fill( self.backcolour , (self.x,self.y,self.width,self.height) )         # blit text        for i,s in enumerate(self.text_surfaces):            #import pdb; pdb.set_trace() # break point            #if i >= self.height_characters:                #break            if i*self.fontheight > self.height and self.wordwrap:                break            #wsurface.blit(s, (self.x,self.y+(i*(self.size-fuck_constant_b*self.size))))            wsurface.blit( s , (self.x,self.y+i*self.fontheight) )