"""    util.py        set of constants and functions that assist the rest of the package"""import osimport euclid# current working directorycwd = os.path.dirname(os.path.realpath( __file__ ))# return a relative pathdef rel( path ):    final_path_ = cwd    res_path_ = path.split("/")        for dir in res_path_:        final_path_ = os.path.join(final_path_, dir)        return final_path_# returns b if it's closer to a or c if it's closer to adef closer ( a , b , c ):    #assert isinstance( a , euclid.Point2 )    #assert isinstance( b , euclid.Point2 )    #assert isinstance( c , euclid.Point2 )        # calculate relative distance to b and c from a    rel_to_b = euclid.Vector2( b.x-a.x , b.y-a.y ).magnitude_squared()    rel_to_c = euclid.Vector2( c.x-a.x , c.y-a.y ).magnitude_squared()        # return the lowest of the two    if rel_to_b <= rel_to_c:        return b    else:        return c