"""    util.py        set of constants and functions that assist the rest of the package"""import osimport random# current working directorycwd = os.path.dirname(os.path.realpath( __file__ ))# return a relative pathdef rel( path ):    final_path_ = cwd    res_path_ = path.split("/")        for dir in res_path_:        final_path_ = os.path.join(final_path_, dir)        return final_path_#def mutate_colour_random ( intensity , colour ):    """        mutates a given colour's r,g,b components by +- 'intensity'        returns the result                colour must be in (red,green,blue) format        returned colour is also in this format    """    # get current colour    (r_cur,g_cur,b_cur) = colour    # generate the preliminary new colour components    cmat = [r_cur+random.randint(-intensity,intensity) , g_cur+random.randint(-intensity,intensity) , b_cur+random.randint(-intensity,intensity)]    # cap the components off at 0-255    for i,c in enumerate(cmat):        if c > 255:            cmat[i] = 255        if c < 0:            cmat[i] = 0    # return the final colour matrix as a colour triple    return (cmat[0],cmat[1],cmat[2])#def add_uniform_to_colour ( value , colour ):    """        adds 'value' to every component of 'colour'        returns the result colour        'colour' argument is not modified                both the colour argument and the returned colour are in (r,g,b) format    """    # get the current colour    (r_cur,g_cur,b_cur) = colour    # generate new colour components    cmat = [r_cur+value , g_cur+value , b_cur+value]    # cap the comonents off at 0-255    for i,c in enumerate(cmat):        if c > 255:            cmat[i] = 255        if c < 0:            cmat[i] = 0    # return the final colour matrix as a colour triple    return (cmat[0],cmat[1],cmat[2])#