"""    util.py        set of constants and functions that assist the rest of the package"""import os# current working directorycwd = os.path.dirname(os.path.realpath( __file__ ))# return a relative pathdef rel( path ):    final_path_ = cwd    res_path_ = path.split("/")        for dir in res_path_:        final_path_ = os.path.join(final_path_, dir)        return final_path_