"""    resource.py        tvm http://www.pygame.org/wiki/LazyImageLoading?parent=CookBook         - ResourceController    Should not be used directly. Other resource managers inherit from this base    class to manage resources    """import pygameimport weakrefimport syssys.path.append("C:/Python27/proj/BetterConsoleFunctions/V3")import bcfout = bcfimport app_constants class ResourceController(object):    def __init__(self, loader):        self.__dict__.update(dict(            names = {},            cache = weakref.WeakValueDictionary(),            loader = loader        ))            def __setattr__(self, name, value):        self.names[name] = value            def __getattr__(self, name):        try:            img = self.cache[name]        except KeyError:            img = self.loader(self.names[name])             self.cache[name] = img            if app_constants.values.print_texture_loading:                out.pl("~~[RESOURCE LOAD] '" + str(name) + "'")        return img            class textureManager(ResourceController):    def __init__(self):        ResourceController.__init__(self, pygame.image.load)